// src/store/index.js
import Vue from 'vue';
import Vuex from 'vuex';

Vue.use(Vuex);

export default new Vuex.Store({
  state: {
    tasks: []
  },
  mutations: {
    ADD_TASK(state, task) {
      state.tasks.push(task);
    },
    EDIT_TASK(state, editedTask) {
      const index = state.tasks.findIndex(task => task.id === editedTask.id);
      if (index !== -1) {
        Vue.set(state.tasks, index, editedTask);
      }
    },
    DELETE_TASK(state, taskId) {
      state.tasks = state.tasks.filter(task => task.id !== taskId);
    },
    TOGGLE_TASK_COMPLETION(state, taskId) {
      const task = state.tasks.find(task => task.id === taskId);
      if (task) {
        task.completed = !task.completed;
      }
    }
  },
  actions: {
    addTask({ commit }, task) {
      commit('ADD_TASK', { ...task, id: Date.now(), completed: false });
    },
    editTask({ commit }, task) {
      commit('EDIT_TASK', task);
    },
    deleteTask({ commit }, taskId) {
      commit('DELETE_TASK', taskId);
    },
    toggleTaskCompletion({ commit }, taskId) {
      commit('TOGGLE_TASK_COMPLETION', taskId);
    }
  },
  getters: {
    tasks: state => state.tasks,
    getTaskById: state => id => state.tasks.find(task => task.id === id)
  }
});
