// src/store/index.js
import { createStore } from 'vuex';

export default createStore({
  state: {
    tasks: []
  },
  mutations: {
    setTasks(state, tasks) {
      state.tasks = tasks;
    },
    addTask(state, task) {
      state.tasks.push(task);
    },
    deleteTask(state, taskId) {
      state.tasks = state.tasks.filter(task => task.id !== taskId);
    },
    toggleTaskCompletion(state, taskId) {
      const task = state.tasks.find(task => task.id === taskId);
      if (task) {
        task.completed = !task.completed;
      }
    }
  },
  actions: {
    async fetchTasks({ commit }) {
      // Example of fetching tasks from an API
      try {
        const response = await fetch('https://jsonplaceholder.typicode.com/todos');
        const tasks = await response.json();
        commit('setTasks', tasks);
      } catch (error) {
        console.error('Error fetching tasks:', error);
      }
    },
    async addTask({ commit }, task) {
      try {
        // Example: post task to API and then commit to state
        const response = await fetch('https://jsonplaceholder.typicode.com/todos', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify(task)
        });
        const newTask = await response.json();
        commit('addTask', newTask);
      } catch (error) {
        console.error('Error adding task:', error);
      }
    },
    async deleteTask({ commit }, taskId) {
      try {
        // Example: delete task from API and then commit to state
        await fetch(`https://jsonplaceholder.typicode.com/todos/${taskId}`, {
          method: 'DELETE'
        });
        commit('deleteTask', taskId);
      } catch (error) {
        console.error('Error deleting task:', error);
      }
    },
    async toggleTaskCompletion({ commit }, taskId) {
      try {
        // Example: update task completion status in API and then commit to state
        const task = { completed: true /* or toggle based on current state */ };
        await fetch(`https://jsonplaceholder.typicode.com/todos/${taskId}`, {
          method: 'PUT',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify(task)
        });
        commit('toggleTaskCompletion', taskId);
      } catch (error) {
        console.error('Error toggling task completion:', error);
      }
    }
  },
  getters: {
    tasks: state => state.tasks
  }
});
